mapper.select:

select SQL_CALC_FOUND_ROWS item_code, item_name, material, proc_duedate, purch_order_quantity, purch_order_date, proc_plan_number
from(
    select item_code, item_name, material, proc_duedate, purch_order_quantity, purch_order_number, aa.proc_plan_number
    from(
        select a.item_code item_code, item_name, material, proc_duedate, proc_plan_number
        from(
            select item_code, item_name, material from item) a right join procurement_plan b on (a.item_code = b.item_code)
            where b.proc_progress = 0)aa left outer join purchase_order_item bb on (aa.proc_plan_number = bb.proc_plan_number)
            )aaa left outer join purchase_order bbb on (aaa.purch_order_number = bbb.purch_order_number)
            where


             (proc_duedate between date(#{startDate}) and date(#{endDate}))
            order by proc_duedate
            limit #{pageNum},#{amount}

select purch_order_number
from purchase_order_item
where proc_plan_number = #{proc_plan_number}

select purch_order_detail, note, item_code, item_name, item_price, business_number, company_name, company_address, manager, manager_tel, proc_plan_number, proc_duedate, purch_order_date
from(
select purch_order_detail, aaaaa.note, item_code, item_name, item_price, business_number, proc_plan_number, proc_duedate, purch_order_date
from(
select purch_order_detail,  note, proc_plan_number, item_code, item_name, item_price, contract_number, proc_duedate, purch_order_date
from(
select purch_order_detail,  note, proc_plan_number, item_code, item_name, proc_duedate, purch_order_date
from(
select purch_order_detail, note, aa.proc_plan_number, item_code, proc_duedate, purch_order_date
from (
select purch_order_detail, purch_order_quantity, note, proc_plan_number, purch_order_date
from(
select purch_order_number, purch_order_detail, purch_order_date
from purchase_order
where purch_order_number = #{purch_order_number}) a inner join purchase_order_item b on (a.purch_order_number = b.purch_order_number)) aa left join procurement_plan bb
on (aa.proc_plan_number = bb.proc_plan_number)) aaa join item bbb using (item_code)) aaaa join contract_item bbbb using (item_code)) aaaaa join contract bbbbb
using (contract_number)) c join `co-op_company` using (business_number)

select SQL_CALC_FOUND_ROWS item_code, item_name, width, length, height, material, prime_quantity, received_in_date, released_in_date, ifnull(item_price,-1) item_price_nonull from( select item_code, item_name, width, length, height, material, prime_quantity, ifnull(receive_in_date,0) received_in_date, ifnull(release_in_date,0) released_in_date from( select item_code, item_name, width, length, height, material, ifnull(primary_quantity,0) prime_quantity from item aa left outer join( select item_code, ifnull(all_receive_quantity,0)-ifnull(all_release_quantity,0) primary_quantity from( select item_code, sum(ifnull(receive_quantity, 0)) all_receive_quantity from receive where receive_date < date(#{startDate}) group by item_code) a left outer join (select item_code, sum(ifnull(release_quantity,0)) all_release_quantity from `release` where release_date < date(#{startDate}) group by item_code) b using (item_code) union select item_code, ifnull(all_receive_quantity,0)-ifnull(all_release_quantity,0) primary_quantity from(select item_code, sum(ifnull(receive_quantity, 0)) all_receive_quantity from receive where receive_date < date(#{startDate}) group by item_code) j right outer join( select item_code, sum(ifnull(release_quantity,0)) all_release_quantity from `release` where release_date < date(#{startDate}) group by item_code) k using (item_code)) bb using(item_code)) aaa left outer join ((select * from( select item_code, sum(ifnull(receive_quantity,0)) receive_in_date from receive where receive_date between date(#{startDate}) and date(#{endDate})+1 group by item_code) c left join (select item_code, sum(ifnull(release_quantity,0)) release_in_date from `release` where release_date between date(#{startDate}) and date(#{endDate})+1 group by item_code) d using (item_code)) union
(select item_code, ifnull(receive_in_date,0), ifnull(release_in_date,0) from( select item_code, sum(ifnull(receive_quantity,0)) receive_in_date from receive where receive_date between date(#{startDate}) and date(#{endDate})+1 group by item_code) c right join (select item_code, sum(ifnull(release_quantity,0)) release_in_date from `release` where release_date between date(#{startDate}) and date(#{endDate})+1 group by item_code) d using (item_code))) bbb using (item_code)) cc left outer join contract_item dd using(item_code) where
order by length(item_code), item_code limit #{pageNum},#{amount}
